#include "gfx/hal/inc/gfx_display.h"

// this array is either defined in a driver simulator module or 
// automatically generated by a code generator in a separate file
extern GFX_DisplayInfo GFX_DisplayInfoList[GFX_DISPLAY_COUNT];

GFX_DisplayInfo* GFX_DisplayInfoPtrGet(GFX_Display idx)
{
    if(idx >= GFX_DISPLAY_COUNT)
        return NULL;
        
    return &GFX_DisplayInfoList[idx];
}